import React, { Component } from 'react';
import {
	StatusBar
} from 'expo-status-bar';
import {
	StyleSheet,
	Text,
	View,
	TouchableOpacity,
	Platform,
	Dimensions,
	SafeAreaView,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import {stylePortrait, styleLandscape} from './styles.tsx';
import * as Haptics from 'expo-haptics';
import Maths from './maths.tsx';

let trigMode = 'Deg';
let maths = new Maths(trigMode)
let styles = stylePortrait;

export default class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			ANS: null,
			ansArr: [],
			inv: false,
			render: false,
			firstRound: 10,
			current: '',
			answer: '',
			orientation: Dimensions.get('screen').height >
			Dimensions.get('screen').width ? 'portrait' : 'landscape',
			map: {
				'--': '+',
				'++': '+',
				'sin(': '* maths.sin(',
				'cos(': '* maths.cos(',
				'tan(': '* maths.tan(',
				'sin-1(': '* maths.sinInv(',
				'cos-1(': '* maths.cosInv(',
				'tan-1(': '* maths.tanInv(',
				'÷': '/',
				'log': '* maths.log',
				'ln': '* maths.ln',
				'√': '* Math.sqrt',
				'×': '* ',
				'e': '* Math.E * ',
				'π': '* Math.PI * ',
				',': '',
				'%': '/100 *',
				'ANS': 'this.state.ANS'	
			},
			opers: [
				'+','×', 
				'÷','MOD'
			],
			inner: [
				'log', 'ln',
				'sin', 'cos',
				'tan', '√',
				'%'
			],
			errorNames:[
				'undefined',
				'Syntax Error',
				'Math Error',
				'NaN'
			]
		};
		this.checkOrientation = this.checkOrientation.bind(this);
	}
	touch(stylesButton, stylesText, innerText, command) {
		return (
			<TouchableOpacity style={stylesButton} onPress={() => {
			Platform.OS != 'web' ? Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light) : null;
			this.appNum(command);
			}}>
				<Text style={stylesText}>{innerText}</Text>
			</TouchableOpacity>
		);
	}
	checkOrientation() {
		let height = Dimensions.get('screen').height;
		let width = Dimensions.get('screen').width;
		if (height > width) {
			this.setState({orientation: 'portrait'});
			styles = stylePortrait;
			this.forceUpdate();
